import { useState } from 'react';
import axios from 'axios';
import { userNavigate } from 'react-router-dom';
import { TextField } from '@mui/material';

const Create = () => {
    const [form, setForm] = useState({});

    const handleForm = (e) => {
        let name = e.target.name;
        let value = e.target.value;

        setForm(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const submitForm = () => {

        let token = localStorage.getItem('token');

        axios.post('https://festivals-api.vercel.app/api/festivals/', { //data en el primer objeto, headers en el 2o

        }, {
            headers: {
                "Authorization": `Bearer ${token}`
            }
        })
        .then(response => {
            console.log(response.data);
            // navigate('/');
        })
        .catch(err => {
            console.error(err);
            console.log(err.response.data)
        })
    };  
    return (
    <>
        <h2>Create</h2>
        <div className='form-group'>
            <TextField variant="filled" label='Title' name='title' onChange={handleForm}/>
        </div>

        <div className='form-group'>
            <TextField variant="filled" label='Description' name='Description' onChange={handleForm}/>
        </div>
    </>
    );
};

export default Create;
