import { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import TextField from '@mui/material/Textfield';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import FormControl from '@mui/material/FormControl';
import MenuItem from '@mui/material/MenuItem';
import Button from '@mui/material/Button';
import FormHelperText from '@mui/material/FormHelperText';

const Create = () => {
    const [form, setForm] = useState({});
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    const handleForm = (e) => {
        let name = e.target.name;
        let value = e.target.value;

        setForm(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const isRequired = (fields) => {
        let error = false;

        fields.forEach(field => {
            if(!form[field]){
                error = true;
                setErrors(prevState => ({
                    ...prevState,
                    [field]: {
                        message: `${field} is required!!!!`
                    }
                }));
            }
        });

        return error;
    };

    const submitForm = () => {

        if(!isRequired(['title', 'description', 'city', 'start_date'])){
            let token = localStorage.getItem('token');

            axios.post('https://festivals-api.vercel.app/api/festivals', form, {
                    headers: {
                        "Authorization": `Bearer ${token}`
                    }
            })
             .then(response => {
                console.log(response.data);
                navigate('/festivals');
             })
             .catch(err => {
                console.error(err);
                console.log(err.response.data.message)
                setErrors(err.response.data.errors);
             });
        }

    };

    return (
        <>
            <h2>Create</h2>

            <div className='form-group'>
                <TextField 
                    variant='filled' 
                    label='Title' 
                    name='title' 
                    onChange={handleForm}
                    error={errors.title}
                    helperText={errors.title?.message}
                />
            </div>
            <div className='form-group'>
                <TextField 
                    multiline 
                    rows={4} 
                    variant='filled' 
                    label='Description' 
                    name='description' 
                    onChange={handleForm}
                    error={errors.description}
                    helperText={errors.description?.message}
                />
            </div>
            <div className='form-group'>
                <FormControl variant='filled' fullWidth error={errors.city}>
                    <InputLabel id='city-select'>City</InputLabel>
                    <Select labelId='city-select' name='city' label='City' onChange={handleForm}>
                        <MenuItem value='Dublin'>Dublin</MenuItem>
                        <MenuItem value='Cork'>Cork</MenuItem>
                        <MenuItem value='Galway'>Galway</MenuItem>
                        <MenuItem value='Mayo'>Mayo</MenuItem>
                        <MenuItem value='Wexford'>Dublin</MenuItem>
                    </Select>
                    <FormHelperText>{errors.city?.message}</FormHelperText>
                </FormControl>
            </div>
            <div className='form-group'>
                <TextField 
                    variant='filled'
                    label='Start Date'
                    type='datetime-local'
                    name='start_date'
                    onChange={handleForm}
                    InputLabelProps={{ shrink: true }}
                    error={errors.start_date}
                    helperText={errors.start_date?.message}
                />
            </div>
            <div className='form-group'>
                <TextField 
                    variant='filled'
                    label='End Date'
                    type='datetime-local'
                    name='end_date'
                    onChange={handleForm}
                    InputLabelProps={{ shrink: true }}
                    error={errors.end_date}
                    helperText={errors.end_date?.message}
                />
            </div>

            <Button onClick={submitForm} variant='contained'>Submit</Button>
        </>
    );
};

export default Create;
