import axios from 'axios';
import { useState, useEffect } from 'react';
import FestivalCard from '../../components/FestivalCard';

const Index = (props) => {
    const [ festivals, setFestivals ] = useState(null);

    useEffect(() => {
        axios.get('https://festivals-api.vercel.app/api/festivals')
             .then((response) => {
                 console.log(response.data);
                 setFestivals(response.data);
             })
             .catch((err) => {
                 console.error(err);
             });
    }, []);


    if(!festivals) return 'Loading...';

    const onDelete = (id) => {
        let token = localStorage.getItem('token');

        axios.delete(`https://festivals-api.vercel.app/api/festivals/${id}`, {
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            })
             .then((response) => {
                 console.log(response.data);
                //  navigate('/festivals'); no hace falta porque ya estamos en festivals
                 
             })
             .catch((err) => {
                 console.error(err);
                 console.log(err.response.data.message);                 
             });
    };


    const festivalsList = festivals.map((festival) => {
        return <FestivalCard festival={festival} authenticated={props.authenticated} onDelete= {onDelete}/>;
    });

    return (
        <>
            <h2>Festivals</h2>
            { festivalsList }
        </>
    );
};

export default Index;
