import DeleteIcon from '@mui/icons-material/Delete';
import  Button  from '@mui/material/Button';

const onDelete = (id) => {
    let token = localStorage.getItem('token');

    axios.delete(`https://festivals-api.vercel.app/api/festivals/${id}`, {
            headers: {
                "Authorization": `Bearer ${token}`
            }
        })
         .then((response) => {
             console.log(response.data);
            //  navigate('/festivals'); no hace falta porque ya estamos en festivals
            
            let festivalsNew = festivals.filter(festival => {
                return festival._id !== id;
            }); 

            setFestivals(festivalsNew);

         })
         .catch((err) => {
             console.error(err);
             console.log(err.response.data.message);                 
         });
};

const DeleteBtn = (props) => {
    return (
        <Button 
                startIcon={<DeleteIcon />}
                variant='outlined'
                color='error'
                onClick={() => props.onDelete(props.id)}
            >
                Delete
            </Button>
    );
};

export default DeleteBtn;
