import { useState, useEffect } from 'react';

const CountDown2 = (props) => {
    let [count, setCount] = useState(props.startValue);

    useEffect(() => {
        console.log('mounted');
    }, []);

    useEffect(() => {
        console.log('count changed');
    }, [count]);

    return <h1>Hi from CountDown 2: {count}</h1>
};

export default CountDown2;
